package excercise;

public class exe4_Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=-42;
		int b=0;
		System.out.println("Value of a and b before swapping"+a+"\t"+b);
		b=b-a;
		a=a+b;
		b=a-b;
		System.out.println("Value of a and b after swapping"+a+"\t"+b);
	}

}
